using BenchmarkDotNet.Running;using TileBasedLightning;var _random = new Random();var map = new[,]{	{ 3, 0, 0, 0, 0, 0, 2, 7, 3, 2 },	{ 1, 15, 15, 15, 15, 15, 1, 6, 8, 10 },	{ 8, 7, 7, 7, 2, 15, 8, 0, 6, 10 },	{ 8, 6, 6, 6, 10, 15, 1, 6, 8, 10 },	{ 8, 6, 6, 6, 10, 15, 1, 6, 8, 10 },	{ 8, 6, 6, 6, 10, 15, 1, 6, 8, 10 },	{ 8, 6, 6, 6, 10, 15, 1, 6, 8, 10 },	{ 8, 6, 6, 6, 10, 15, 1, 6, 8, 10 },	{ 8, 6, 6, 6, 10, 15, 1, 6, 8, 10 },	{ 8, 6, 6, 6, 10, 15, 13, 6, 8, 10 },	{ 4, 9, 9, 9, 5, 9, 9, 9, 4, 5 }};var mapSize = new Vector2Int(map.GetLength(0), map.GetLength(1));var tiles = new Tile[mapSize.x, mapSize.y];for (var x = 0; x < mapSize.x; x++)for (var y = 0; y < mapSize.y; y++)	tiles[x, y] = new Tile(x, y, (Tile.Type)map[x, y]);// for (var i = 0; i < 50; i++)// {// 	var x = _random.Next(0, mapSize.x);// 	var y = _random.Next(0, mapSize.y);// 	while (map[x, y] == 15)// 	{// 		x = _random.Next(0, mapSize.x);// 		y = _random.Next(0, mapSize.y);// 	}// 	Lightning.CalculateLightning(ref tiles, x, y, Color.white);// }Lightning.CalculateLightning(ref tiles, 4, 6, Color.white);Lightning.CalculateLightning(ref tiles, 4, 2, Color.white);// Printing mapfor (var x = 0; x < tiles.GetLength(0); x++){	for (var y = 0; y < tiles.GetLength(1); y++)	{		if (tiles[x, y].illuminationIntensity < 0.001f)		{			Console.Write("\t");			continue;		}		if((x, y) == (4,6))		{			Console.Write("*\t");			continue;		}		if((x, y) == (4,2))		{			Console.Write("*\t");			continue;		}		Console.Write($"{tiles[x, y].illuminationIntensity:F1}\t");	}	Console.WriteLine();}Console.WriteLine();Console.WriteLine();Console.WriteLine();Console.WriteLine("Press Y to run Benchmark");if(Console.ReadKey().Key == ConsoleKey.Y)	BenchmarkRunner.Run<Bench>();